import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated, isLabourerAuthenticated, requireRole } from "./replitAuth";
import bcrypt from "bcrypt";
import {
  ObjectStorageService,
  ObjectNotFoundError,
} from "./objectStorage";
import {
  insertEmployeeTypeSchema,
  insertProjectSchema,
  insertLabourerSchema,
  insertPayRateSchema,
  insertWorkLogSchema,
  insertPaymentPeriodSchema,
  insertCorrectionRequestSchema,
  updateUserRoleSchema,
  updateUserSchema,
  paymentPeriodEntries,
  paymentPeriods,
  projects,
  labourers,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc } from "drizzle-orm";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // ============= Authentication Routes =============
  app.get('/api/user', isAuthenticated, async (req: any, res) => {
    try {
      // Look up by email (not sub) to handle OIDC sub rotation
      const userEmail = req.user.claims.email;
      const user = await storage.getUserByEmail(userEmail);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      // Look up by email (not sub) to handle OIDC sub rotation
      const userEmail = req.user.claims.email;
      const user = await storage.getUserByEmail(userEmail);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // ============= User Management Routes =============
  app.get('/api/users', isAuthenticated, requireRole("super_admin", "admin"), async (req: any, res) => {
    try {
      const users = await storage.getAllUsers();
      res.json(users);
    } catch (error) {
      console.error("Error fetching users:", error);
      res.status(500).json({ message: "Failed to fetch users" });
    }
  });

  app.put('/api/users/:id', isAuthenticated, requireRole("super_admin", "admin"), async (req: any, res) => {
    try {
      const updateData = updateUserSchema.parse(req.body);

      // Users cannot edit themselves
