import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Textarea } from "@/components/ui/textarea";
import { FileUpload } from "@/components/file-upload";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { validateSAId, formatDateForInput } from "@/lib/saIdValidation";
import { insertLabourerSchema, type Labourer, type Project, type EmployeeType, type User } from "@shared/schema";
import { z } from "zod";
import { UserPlus, Search, Eye, Info, Pencil } from "lucide-react";
import { format } from "date-fns";
import { Alert, AlertDescription } from "@/components/ui/alert";

type LabourerFormData = z.infer<typeof insertLabourerSchema>;

// South African Banks with Universal Branch Codes
const SA_BANKS = [
  { name: "Absa Bank", universalBranchCode: "632005" },
  { name: "Capitec Bank", universalBranchCode: "470010" },
  { name: "First National Bank (FNB)", universalBranchCode: "250655" },
  { name: "Investec Bank", universalBranchCode: "580105" },
  { name: "Nedbank", universalBranchCode: "198765" },
  { name: "Standard Bank", universalBranchCode: "051001" },
  { name: "African Bank", universalBranchCode: "430000" },
  { name: "Mercantile Bank", universalBranchCode: "450905" },
  { name: "TymeBank", universalBranchCode: "678910" },
  { name: "Bidvest Bank", universalBranchCode: "679000" },
  { name: "Sasfin Bank", universalBranchCode: "683000" },
  { name: "Bank of Athens", universalBranchCode: "410506" },
  { name: "RMB Private Bank", universalBranchCode: "222026" },
  { name: "South African Post Bank (Post Office)", universalBranchCode: "460005" },
  { name: "Hollard Bank", universalBranchCode: "585001" },
  { name: "Discovery Bank", universalBranchCode: "679000" },
  { name: "Standard Chartered Bank", universalBranchCode: "730020" },
  { name: "Barclays Bank", universalBranchCode: "590000" },
  { name: "Investec Bank Limited", universalBranchCode: "580105" },
  { name: "Mercantile Bank Limited", universalBranchCode: "450905" },
] as const;

export default function LabourersPage() {
  const { toast } = useToast();
  const [dialogOpen, setDialogOpen] = useState(false);
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [filterProject, setFilterProject] = useState<string>("all");
  const [filterEmployeeType, setFilterEmployeeType] = useState<string>("all");
  const [selectedLabourer, setSelectedLabourer] = useState<Labourer | null>(null);
  const [viewDialogOpen, setViewDialogOpen] = useState(false);

  // Get current user for authorization
  const { data: currentUser } = useQuery<User>({
    queryKey: ["/api/user"],
  });

  // Determine if user can create labourers
  const canCreate = currentUser?.role && ["super_admin", "admin", "project_manager", "supervisor", "project_admin"].includes(currentUser.role);

  // Fetch projects
  const { data: projects } = useQuery<Project[]>({
    queryKey: ["/api/projects"],
  });

  // Fetch employee types
  const { data: employeeTypes, isLoading: isLoadingEmployeeTypes } = useQuery<EmployeeType[]>({
    queryKey: ["/api/employee-types"],
  });

  // Fetch labourers for all projects AND unassigned labourers
  const [allLabourers, setAllLabourers] = useState<Labourer[]>([]);
  const projectsData = projects || [];

  useEffect(() => {
    const fetchAllLabourers = async () => {
      // Fetch labourers from all projects
      const labourersByProject = await Promise.all(
        projectsData.map(async (project) => {
          try {
            const response = await fetch(`/api/projects/${project.id}/labourers`);
            if (response.ok) {
              return await response.json();
            }
            return [];
          } catch {
            return [];
          }
        })
      );

      // Fetch unassigned labourers (not assigned to any project)
      let unassignedLabourers: Labourer[] = [];
      try {
        const response = await fetch(`/api/labourers/available`);
        if (response.ok) {
          unassignedLabourers = await response.json();
        }
      } catch {
        // Silent fail - continue with just project-assigned labourers
      }

      // Combine all labourers (from projects + unassigned)
      const allCombined = [...labourersByProject.flat(), ...unassignedLabourers];

      // Remove duplicates (in case a labourer appears in both lists)
      const uniqueLabourers = Array.from(
        new Map(allCombined.map(l => [l.id, l])).values()
      );

      setAllLabourers(uniqueLabourers);
    };

    fetchAllLabourers();
  }, [projectsData.length]);

  const form = useForm<LabourerFormData>({
    resolver: zodResolver(insertLabourerSchema),
    defaultValues: {
      userId: undefined,
      projectId: "",
      employeeTypeId: "",
      firstName: "",
      surname: "",
      idNumber: "",
      dateOfBirth: "",
      gender: "",
      contactNumber: "",
      email: "",
      physicalAddress: "",
      profilePhotoPath: undefined,
      idDocumentPath: undefined,
      bankName: "",
      accountNumber: "",
      accountType: "cheque",
      branchCode: "",
      bankingProofPath: undefined,
      createdBy: currentUser?.id || "",
    },
  });

  // Watch ID number for auto-population (only for SA IDs, not passports)
  const idNumber = form.watch("idNumber");
  const bankName = form.watch("bankName");

  useEffect(() => {
    if (idNumber && idNumber.length >= 6) {
      const idInfo = validateSAId(idNumber);

      // Clear previous ID errors
      form.clearErrors("idNumber");

      if (idInfo.isValid) {
        // Auto-populate DOB and gender only for SA IDs (not passports)
        if (idInfo.dateOfBirth && idInfo.gender) {
          form.setValue("dateOfBirth", formatDateForInput(idInfo.dateOfBirth));
          form.setValue("gender", idInfo.gender);
        }
      } else if (idInfo.error && idNumber.length >= 9) {
        // Only show error if user has entered enough characters
        form.setError("idNumber", { message: idInfo.error });
      }
    }
  }, [idNumber, form]);

  // Auto-populate branch code when bank is selected
  useEffect(() => {
    if (bankName) {
      const selectedBank = SA_BANKS.find(bank => bank.name === bankName);
      if (selectedBank) {
        form.setValue("branchCode", selectedBank.universalBranchCode);
      }
    }
  }, [bankName, form]);

  const createMutation = useMutation({
    mutationFn: async (data: LabourerFormData) => {
      return apiRequest("POST", "/api/labourers", { ...data, createdBy: currentUser?.id });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Labourer onboarded successfully",
      });
      form.reset();
      setDialogOpen(false);
      // Refetch labourers
      const projectId = form.getValues("projectId");
      if (projectId) {
        queryClient.invalidateQueries({ queryKey: [`/api/projects/${projectId}/labourers`] });
      }
      // Refresh all labourers (both assigned and unassigned)
      const fetchAllLabourers = async () => {
        // Fetch labourers from all projects
        const labourersByProject = await Promise.all(
          projectsData.map(async (project) => {
            try {
              const response = await fetch(`/api/projects/${project.id}/labourers`);
              if (response.ok) {
                return await response.json();
              }
              return [];
            } catch {
              return [];
            }
          })
        );

        // Fetch unassigned labourers
        let unassignedLabourers: Labourer[] = [];
        try {
          const response = await fetch(`/api/labourers/available`);
          if (response.ok) {
            unassignedLabourers = await response.json();
          }
        } catch {
          // Silent fail
        }

        // Combine and deduplicate
        const allCombined = [...labourersByProject.flat(), ...unassignedLabourers];
        const uniqueLabourers = Array.from(
          new Map(allCombined.map(l => [l.id, l])).values()
        );

        setAllLabourers(uniqueLabourers);
      };
      fetchAllLabourers();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to onboard labourer",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: LabourerFormData) => {
    createMutation.mutate(data);
  };

  // Edit form
  const editForm = useForm<LabourerFormData>({
    resolver: zodResolver(insertLabourerSchema.partial()),
    defaultValues: {
      userId: undefined,
      projectId: "",
      employeeTypeId: "",
      firstName: "",
      surname: "",
      idNumber: "",
      dateOfBirth: "",
      gender: "",
      contactNumber: "",
      email: "",
      physicalAddress: "",
      profilePhotoPath: undefined,
      idDocumentPath: undefined,
      bankName: "",
      accountNumber: "",
      accountType: "cheque",
      branchCode: "",
      bankingProofPath: undefined,
    },
  });

  // Watch edit form fields for auto-population
  const editIdNumber = editForm.watch("idNumber");
  const editBankName = editForm.watch("bankName");

  // Auto-populate DOB and gender from ID number in edit form
  useEffect(() => {
    if (editIdNumber && editIdNumber.length >= 6) {
      const idInfo = validateSAId(editIdNumber);

      editForm.clearErrors("idNumber");

      if (idInfo.isValid) {
        if (idInfo.dateOfBirth && idInfo.gender) {
          editForm.setValue("dateOfBirth", formatDateForInput(idInfo.dateOfBirth));
          editForm.setValue("gender", idInfo.gender);
        }
      } else if (idInfo.error && editIdNumber.length >= 9) {
        editForm.setError("idNumber", { message: idInfo.error });
      }
    }
  }, [editIdNumber, editForm]);

  // Auto-populate branch code in edit form
  useEffect(() => {
    if (editBankName) {
      const selectedBank = SA_BANKS.find(bank => bank.name === editBankName);
      if (selectedBank) {
        editForm.setValue("branchCode", selectedBank.universalBranchCode);
      }
    }
  }, [editBankName, editForm]);

  // Populate edit form when labourer is selected
  useEffect(() => {
    if (selectedLabourer && editDialogOpen) {
      editForm.reset({
        userId: selectedLabourer.userId || undefined,
        projectId: selectedLabourer.projectId || "",
        employeeTypeId: selectedLabourer.employeeTypeId,
        firstName: selectedLabourer.firstName,
        surname: selectedLabourer.surname,
        idNumber: selectedLabourer.idNumber,
        dateOfBirth: selectedLabourer.dateOfBirth || "",
        gender: selectedLabourer.gender || "",
        contactNumber: selectedLabourer.contactNumber,
        email: selectedLabourer.email || "",
        physicalAddress: selectedLabourer.physicalAddress || "",
        profilePhotoPath: selectedLabourer.profilePhotoPath || undefined,
        idDocumentPath: selectedLabourer.idDocumentPath || undefined,
        bankName: selectedLabourer.bankName,
        accountNumber: selectedLabourer.accountNumber,
        accountType: selectedLabourer.accountType || "cheque",
        branchCode: selectedLabourer.branchCode,
        bankingProofPath: selectedLabourer.bankingProofPath || undefined,
      });
    }
  }, [selectedLabourer, editDialogOpen, editForm]);

  const updateMutation = useMutation({
    mutationFn: async (data: Partial<LabourerFormData>) => {
      if (!selectedLabourer) throw new Error("No labourer selected");
      return apiRequest("PUT", `/api/labourers/${selectedLabourer.id}`, data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Labourer details updated successfully",
      });
      setEditDialogOpen(false);
      setSelectedLabourer(null);
      // Refresh all labourers
      const fetchAllLabourers = async () => {
        const labourersByProject = await Promise.all(
          projectsData.map(async (project) => {
            try {
              const response = await fetch(`/api/projects/${project.id}/labourers`);
              if (response.ok) {
                return await response.json();
              }
              return [];
            } catch {
              return [];
            }
          })
        );

        let unassignedLabourers: Labourer[] = [];
        try {
          const response = await fetch(`/api/labourers/available`);
          if (response.ok) {
            unassignedLabourers = await response.json();
          }
        } catch {
          // Silent fail
        }

        const allCombined = [...labourersByProject.flat(), ...unassignedLabourers];
        const uniqueLabourers = Array.from(
          new Map(allCombined.map(l => [l.id, l])).values()
        );

        setAllLabourers(uniqueLabourers);
      };
      fetchAllLabourers();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update labourer",
        variant: "destructive",
      });
    },
  });

  const onEditSubmit = (data: LabourerFormData) => {
    updateMutation.mutate(data);
  };

  // Filter labourers
  const filteredLabourers = allLabourers.filter((labourer) => {
    const matchesSearch = 
      `${labourer.firstName} ${labourer.surname}`.toLowerCase().includes(searchQuery.toLowerCase()) ||
      labourer.idNumber.toLowerCase().includes(searchQuery.toLowerCase()) ||
      labourer.contactNumber.includes(searchQuery);

    const matchesProject = filterProject === "all" || labourer.projectId === filterProject;
    const matchesEmployeeType = filterEmployeeType === "all" || labourer.employeeTypeId === filterEmployeeType;

    return matchesSearch && matchesProject && matchesEmployeeType;
  });

  return (
    <div className="p-6 space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold" data-testid="heading-labourers">Labourers</h1>
          <p className="text-muted-foreground">Manage labourer onboarding and information</p>
        </div>
        {canCreate && (
          <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
            <DialogTrigger asChild>
              <Button data-testid="button-new-labourer">
                <UserPlus className="h-4 w-4 mr-2" />
                Onboard Labourer
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle>Onboard New Labourer</DialogTitle>
              </DialogHeader>

              <Alert className="bg-blue-50 dark:bg-blue-950 border-blue-200 dark:border-blue-800">
                <Info className="h-4 w-4 text-blue-600 dark:text-blue-400" />
                <AlertDescription className="text-sm text-blue-900 dark:text-blue-100">
                  A login account will be automatically created for this labourer. They can log in using their <strong>contact number or email</strong> as username and <strong>ID number/passport</strong> as password.
                </AlertDescription>
              </Alert>

              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                  <div className="grid grid-cols-2 gap-4">
                    {/* Project Selection */}
                    <FormField
                      control={form.control}
                      name="projectId"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Project *</FormLabel>
                          <Select onValueChange={field.onChange} value={field.value || undefined}>
                            <FormControl>
                              <SelectTrigger data-testid="select-project">
                                <SelectValue placeholder="Select project" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              {projects?.map((project) => (
                                <SelectItem key={project.id} value={project.id}>
                                  {project.name}
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    {/* Employee Type */}
                    <FormField
                      control={form.control}
                      name="employeeTypeId"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Employee Type *</FormLabel>
                          <Select onValueChange={field.onChange} value={field.value} disabled={isLoadingEmployeeTypes}>
                            <FormControl>
                              <SelectTrigger data-testid="select-employee-type">
                                <SelectValue placeholder={isLoadingEmployeeTypes ? "Loading..." : "Select employee type"} />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              {isLoadingEmployeeTypes ? (
                                <div className="p-2 text-sm text-muted-foreground">Loading employee types...</div>
                              ) : employeeTypes && employeeTypes.length > 0 ? (
                                employeeTypes.map((type) => (
                                  <SelectItem key={type.id} value={type.id}>
                                    {type.name}
                                  </SelectItem>
                                ))
                              ) : (
                                <div className="p-2 text-sm text-muted-foreground">No employee types found</div>
                              )}
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    {/* First Name */}
                    <FormField
                      control={form.control}
                      name="firstName"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>First Name *</FormLabel>
                          <FormControl>
                            <Input {...field} data-testid="input-first-name" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
