/**
 * South African ID Number Validation and Extraction Utility
 * 
 * SA ID Format: YYMMDD SSSS C A Z (13 digits)
 * - YYMMDD: Date of birth (year, month, day)
 * - SSSS: Gender indicator (0000-4999 = female, 5000-9999 = male)
 * - C: Citizenship (0 = SA citizen, 1 = permanent resident)
 * - A: Usually 8 or 9
 * - Z: Checksum digit (Luhn algorithm)
 */

export interface SAIdInfo {
  isValid: boolean;
  dateOfBirth: Date | null;
  gender: "male" | "female" | null;
  isCitizen: boolean | null;
  error?: string;
}

/**
 * Validates and extracts information from a South African ID number or passport
 */
export function validateSAId(idNumber: string): SAIdInfo {
  // Remove any spaces or dashes
  const cleanId = idNumber.replace(/[\s-]/g, "").toUpperCase();

  // Check if it's a passport (6-9 alphanumeric characters)
  if (/^[A-Z0-9]{6,9}$/.test(cleanId) && !/^[0-9]{13}$/.test(cleanId)) {
    // Valid passport - no SA-specific metadata available
    return {
      isValid: true,
      dateOfBirth: null,
      gender: null,
      isCitizen: null,
    };
  }

  // Check if it's exactly 13 digits (SA ID)
  if (!/^[0-9]{13}$/.test(cleanId)) {
    return {
      isValid: false,
      dateOfBirth: null,
      gender: null,
      isCitizen: null,
      error: "Must be a valid SA ID (13 digits) or Passport (6-9 characters)",
    };
  }

  // Extract components
  const yearStr = cleanId.substring(0, 2);
  const monthStr = cleanId.substring(2, 4);
  const dayStr = cleanId.substring(4, 6);
  const genderDigits = cleanId.substring(6, 10);
  const citizenDigit = cleanId.substring(10, 11);
  const checksumDigit = parseInt(cleanId.substring(12, 13));

  // Parse date components
  const month = parseInt(monthStr, 10);
  const day = parseInt(dayStr, 10);

  // Determine century (assume anyone born after current year is from 1900s)
  const currentYear = new Date().getFullYear();
  const currentCentury = Math.floor(currentYear / 100) * 100;
  const yearTwoDigit = parseInt(yearStr, 10);
  let year = currentCentury + yearTwoDigit;

  // If the year is in the future, assume it's from the previous century
  if (year > currentYear) {
    year -= 100;
  }

  // Validate date
  const dateOfBirth = new Date(year, month - 1, day);
  if (
    dateOfBirth.getFullYear() !== year ||
    dateOfBirth.getMonth() !== month - 1 ||
    dateOfBirth.getDate() !== day ||
    month < 1 ||
    month > 12 ||
    day < 1 ||
    day > 31
  ) {
    return {
      isValid: false,
      dateOfBirth: null,
      gender: null,
      isCitizen: null,
      error: "Invalid date in ID number",
    };
  }

  // Validate age (must be at least 16 years old)
  const age = Math.floor(
    (Date.now() - dateOfBirth.getTime()) / (365.25 * 24 * 60 * 60 * 1000)
  );
  if (age < 16) {
    return {
      isValid: false,
      dateOfBirth,
      gender: null,
      isCitizen: null,
      error: "Person must be at least 16 years old",
    };
  }

  // Determine gender (0000-4999 = female, 5000-9999 = male)
  const genderValue = parseInt(genderDigits, 10);
  const gender = genderValue < 5000 ? "female" : "male";

  // Determine citizenship
  const isCitizen = citizenDigit === "0";

  // Validate checksum using Luhn algorithm
  if (!validateLuhnChecksum(cleanId)) {
    return {
      isValid: false,
      dateOfBirth,
      gender,
      isCitizen,
      error: "Invalid ID number checksum",
    };
  }

  return {
    isValid: true,
    dateOfBirth,
    gender,
    isCitizen,
  };
}

/**
 * Validates the checksum digit using the Luhn algorithm
 * SA IDs include the checksum in the sum and check if total % 10 == 0
 */
function validateLuhnChecksum(idNumber: string): boolean {
  let sum = 0;

  // Process all 13 digits from left to right
  for (let i = 0; i < 13; i++) {
    let digit = parseInt(idNumber[i], 10);

    // Double every second digit from the left (positions with odd index: 1, 3, 5, 7, 9, 11)
    if (i % 2 === 1) {
      digit *= 2;
      // If doubling produces a number > 9, subtract 9
      if (digit > 9) {
        digit -= 9;
      }
    }

    sum += digit;
  }

  // Valid if sum is divisible by 10
  return (sum % 10) === 0;
}

/**
 * Formats a date as YYYY-MM-DD for date input fields
 */
export function formatDateForInput(date: Date | null): string {
  if (!date) return "";
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, "0");
  const day = String(date.getDate()).padStart(2, "0");
  return `${year}-${month}-${day}`;
}

client/src/pages/pay-rates.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Plus, Loader2, DollarSign, History } from "lucide-react";
import { insertPayRateSchema } from "@shared/schema";
import type { User, Project, PayRate, EmployeeType } from "@shared/schema";
import type { z } from "zod";
import { format } from "date-fns";

interface PayRatesPageProps {
  user: User;
}

type PayRateFormData = z.infer<typeof insertPayRateSchema>;

const categoryLabels: Record<string, string> = {
  open_trenching: "Open Trenching",
  close_trenching: "Close Trenching",
  custom: "Custom",
};

const unitLabels: Record<string, string> = {
  per_meter: "Per Meter",
  per_day: "Per Day",
  fixed: "Fixed Amount",
};

export default function PayRatesPage({ user }: PayRatesPageProps) {
  const { toast } = useToast();
  const [addDialogOpen, setAddDialogOpen] = useState(false);
  const [selectedProjectId, setSelectedProjectId] = useState<string>("");
  const [historyDialogOpen, setHistoryDialogOpen] = useState(false);
  const [selectedHistory, setSelectedHistory] = useState<{
    projectName: string;
    employeeTypeName: string;
    category: string;
    rates: PayRate[];
  } | null>(null);

  const canManage = 
    user.role === "super_admin" || 
    user.role === "admin" || 
    user.role === "project_manager";

  const form = useForm<PayRateFormData>({
    resolver: zodResolver(insertPayRateSchema),
    defaultValues: {
      projectId: "",
      employeeTypeId: "",
      category: "open_trenching",
      categoryName: "",
      amount: "",
      unit: "per_meter",
      effectiveDate: new Date().toISOString().split("T")[0],
      createdBy: "",
    },
  });

  const { data: projects } = useQuery<Project[]>({
    queryKey: ["/api/projects"],
  });

  // Auto-select first project when projects load
  useEffect(() => {
    if (!selectedProjectId && projects && projects.length > 0) {
      setSelectedProjectId(projects[0].id);
    }
  }, [projects, selectedProjectId]);

  const { data: employeeTypes } = useQuery<EmployeeType[]>({
    queryKey: ["/api/employee-types"],
  });

  const { data: payRates, isLoading } = useQuery<PayRate[]>({
    queryKey: [`/api/projects/${selectedProjectId}/pay-rates`],
    enabled: !!selectedProjectId,
  });

  const createMutation = useMutation({
    mutationFn: async (data: Omit<PayRateFormData, "createdBy">) => {
      return apiRequest("POST", "/api/pay-rates", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/projects/${selectedProjectId}/pay-rates`] });
      toast({
        title: "Pay rate created",
        description: "The pay rate has been created successfully.",
      });
      setAddDialogOpen(false);
      form.reset();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create pay rate",
        variant: "destructive",
      });
    },
  });

  const handleAdd = (data: PayRateFormData) => {
    const { createdBy, ...rateData } = data;
    // Ensure projectId is included (it's not bound to a form control)
    const completeData = {
      ...rateData,
      projectId: selectedProjectId,
    };
    createMutation.mutate(completeData);
  };

  const viewHistory = (
    projectName: string,
    employeeTypeName: string,
    categoryLabel: string,
    categoryKey: string,
    employeeTypeId: string,
    categoryName?: string
  ) => {
    const filteredRates = payRates?.filter((r) => {
      const employeeTypeMatch = r.employeeTypeId === employeeTypeId;
      const categoryMatch = r.category === categoryKey;

      // For custom categories, also check categoryName to differentiate between different custom categories
      if (categoryKey === "custom" && categoryName) {
        return employeeTypeMatch && categoryMatch && r.categoryName === categoryName;
      }

      return employeeTypeMatch && categoryMatch;
    }) || [];

    setSelectedHistory({
      projectName,
      employeeTypeName,
      category: categoryLabel,
      rates: filteredRates,
    });
    setHistoryDialogOpen(true);
  };

  // Group pay rates by employee type and category
  // For custom categories, also include categoryName in the key to differentiate them
  const groupedRates = payRates?.reduce((acc, rate) => {
    const key = rate.category === "custom" && rate.categoryName
      ? `${rate.employeeTypeId}-${rate.category}-${rate.categoryName}`
      : `${rate.employeeTypeId}-${rate.category}`;
    if (!acc[key]) {
      acc[key] = [];
    }
    acc[key].push(rate);
    return acc;
  }, {} as Record<string, PayRate[]>);

  // Get the latest rate for each employee type/category combination
  const latestRates = Object.entries(groupedRates || {}).map(([key, rates]) => {
    const sorted = rates.sort((a, b) => 
      new Date(b.effectiveDate).getTime() - new Date(a.effectiveDate).getTime()
    );
    return {
      key,
      latest: sorted[0],
      historyCount: sorted.length,
    };
  });

  const selectedProject = projects?.find(p => p.id === selectedProjectId);

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-semibold">Pay Rates</h1>
          <p className="text-sm text-muted-foreground mt-1">
            Configure pay rates per project and employee type
          </p>
        </div>
        {canManage && selectedProjectId && (
          <Button
            onClick={() => {
              form.reset({
                projectId: selectedProjectId,
                employeeTypeId: "",
                category: "open_trenching",
                categoryName: "",
                amount: "",
                unit: "per_meter",
                effectiveDate: new Date().toISOString().split("T")[0],
                createdBy: "",
              });
              setAddDialogOpen(true);
            }}
            data-testid="button-add-rate"
          >
            <Plus className="h-4 w-4 mr-2" />
            Add Pay Rate
          </Button>
        )}
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Select Project</CardTitle>
        </CardHeader>
        <CardContent>
          <Select value={selectedProjectId} onValueChange={setSelectedProjectId}>
            <SelectTrigger data-testid="select-project">
              <SelectValue placeholder="Choose a project to view pay rates" />
            </SelectTrigger>
            <SelectContent>
              {projects?.map((project) => (
                <SelectItem key={project.id} value={project.id}>
                  {project.name} - {project.location || "No location"}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </CardContent>
      </Card>

      {selectedProjectId && (
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle>
                Pay Rates - {selectedProject?.name}
              </CardTitle>
              <Badge variant="outline">
                <DollarSign className="h-3 w-3 mr-1" />
                {latestRates.length} Active Rate{latestRates.length !== 1 ? "s" : ""}
              </Badge>
            </div>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <div className="flex items-center justify-center py-12">
                <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
              </div>
            ) : latestRates.length > 0 ? (
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Employee Type</TableHead>
                    <TableHead>Category</TableHead>
                    <TableHead>Amount</TableHead>
                    <TableHead>Unit</TableHead>
                    <TableHead>Effective Date</TableHead>
                    <TableHead className="text-right">Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {latestRates.map(({ key, latest, historyCount }) => {
                    const employeeType = employeeTypes?.find(
                      (et) => et.id === latest.employeeTypeId
                    );
                    return (
                      <TableRow key={key} data-testid={`row-rate-${latest.id}`}>
                        <TableCell className="font-medium">
                          {employeeType?.name || "Unknown"}
                        </TableCell>
                        <TableCell>
                          {latest.category === "custom" && latest.categoryName
                            ? latest.categoryName
                            : categoryLabels[latest.category] || latest.category}
                        </TableCell>
                        <TableCell className="font-mono">
                          R {Number(latest.amount).toFixed(2)}
                        </TableCell>
                        <TableCell>
                          <Badge variant="secondary">
                            {unitLabels[latest.unit] || latest.unit}
                          </Badge>
                        </TableCell>
                        <TableCell className="text-muted-foreground">
                          {format(new Date(latest.effectiveDate), "MMM d, yyyy")}
                        </TableCell>
                        <TableCell className="text-right">
                          {historyCount > 1 && (
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => {
                                const categoryLabel = latest.category === "custom" && latest.categoryName
                                  ? latest.categoryName
                                  : categoryLabels[latest.category] || latest.category;
                                viewHistory(
                                  selectedProject?.name || "",
                                  employeeType?.name || "",
                                  categoryLabel,
                                  latest.category,
                                  latest.employeeTypeId,
                                  latest.categoryName || undefined
                                );
                              }}
                              data-testid={`button-history-${latest.id}`}
                            >
                              <History className="h-4 w-4 mr-2" />
                              History ({historyCount})
                            </Button>
                          )}
                        </TableCell>
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>
            ) : (
              <div className="text-center py-12 text-muted-foreground">
                No pay rates configured for this project yet.
                {canManage && " Click 'Add Pay Rate' to get started."}
              </div>
            )}
          </CardContent>
        </Card>
      )}

      {/* Add Pay Rate Dialog */}
      <Dialog open={addDialogOpen} onOpenChange={setAddDialogOpen}>
        <DialogContent data-testid="dialog-add-rate">
          <DialogHeader>
            <DialogTitle>Add Pay Rate</DialogTitle>
            <DialogDescription>
              Create a new pay rate for {selectedProject?.name}. You can add historical rates by setting an effective date.
            </DialogDescription>
          </DialogHeader>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(handleAdd)} className="space-y-4">
              <FormField
                control={form.control}
                name="employeeTypeId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Employee Type *</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-employee-type">
                          <SelectValue placeholder="Select employee type" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {employeeTypes?.filter(et => et.isActive).map((type) => (
                          <SelectItem key={type.id} value={type.id}>
                            {type.name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="category"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Category *</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-category">
                          <SelectValue placeholder="Select category" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="open_trenching">Open Trenching</SelectItem>
                        <SelectItem value="close_trenching">Close Trenching</SelectItem>
                        <SelectItem value="custom">Custom</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
              {form.watch("category") === "custom" && (
                <FormField
                  control={form.control}
                  name="categoryName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Custom Category Name *</FormLabel>
                      <FormControl>
                        <Input
                          placeholder="e.g., Cable Installation"
                          {...field}
                          value={field.value || ""}
                          data-testid="input-category-name"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              )}
              <FormField
                control={form.control}
                name="amount"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Amount (ZAR) *</FormLabel>
                    <FormControl>
                      <Input
                        type="text"
                        inputMode="decimal"
                        placeholder="e.g., 25.50"
                        {...field}
                        value={field.value || ""}
                        onChange={(e) => {
                          const value = e.target.value;
                          if (value === "" || /^\d*\.?\d*$/.test(value)) {
                            field.onChange(value);
                          }
                        }}
                        data-testid="input-amount"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="unit"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Unit *</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-unit">
                          <SelectValue placeholder="Select unit" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="per_meter">Per Meter</SelectItem>
                        <SelectItem value="per_day">Per Day</SelectItem>
                        <SelectItem value="fixed">Fixed Amount</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="effectiveDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Effective Date *</FormLabel>
                    <FormControl>
                      <Input
                        type="date"
                        {...field}
                        data-testid="input-effective-date"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <DialogFooter>
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setAddDialogOpen(false)}
                  data-testid="button-cancel"
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  disabled={createMutation.isPending}
                  data-testid="button-submit"
                >
                  {createMutation.isPending && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                  Create Rate
                </Button>
              </DialogFooter>
            </form>
          </Form>
        </DialogContent>
      </Dialog>

      {/* Rate History Dialog */}
      <Dialog open={historyDialogOpen} onOpenChange={setHistoryDialogOpen}>
        <DialogContent className="max-w-3xl" data-testid="dialog-history">
          <DialogHeader>
            <DialogTitle>
              Rate History: {selectedHistory?.employeeTypeName} - {selectedHistory?.category}
            </DialogTitle>
            <DialogDescription>
              Historical pay rates for {selectedHistory?.projectName}
            </DialogDescription>
          </DialogHeader>
          <div className="max-h-96 overflow-auto">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Effective Date</TableHead>
                  <TableHead>Amount</TableHead>
                  <TableHead>Unit</TableHead>
                  <TableHead>Created</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {selectedHistory?.rates
                  .sort((a, b) => 
                    new Date(b.effectiveDate).getTime() - new Date(a.effectiveDate).getTime()
                  )
                  .map((rate, index) => (
                    <TableRow key={rate.id} data-testid={`history-row-${index}`}>
                      <TableCell className="font-medium">
                        {format(new Date(rate.effectiveDate), "MMM d, yyyy")}
                        {index === 0 && (
                          <Badge variant="default" className="ml-2">Current</Badge>
                        )}
                      </TableCell>
                      <TableCell className="font-mono">
                        R {Number(rate.amount).toFixed(2)}
                      </TableCell>
                      <TableCell>
                        {unitLabels[rate.unit] || rate.unit}
                      </TableCell>
                      <TableCell className="text-muted-foreground">
                        {rate.createdAt ? format(new Date(rate.createdAt), "MMM d, yyyy HH:mm") : ""}
                      </TableCell>
                    </TableRow>
                  ))}
              </TableBody>
            </Table>
          </div>
          <DialogFooter>
            <Button
              onClick={() => setHistoryDialogOpen(false)}
              data-testid="button-close-history"
            >
              Close
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/work-log.tsx.content:
import { useState, useEffect, useRef } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { format } from "date-fns";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Calendar } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { CalendarIcon, Save, Loader2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import type { Project, Labourer, PayRate, User } from "@shared/schema";

interface WorkLogEntry {
  labourerId: string;
  labourerName: string;
  employeeTypeId: string;
  openMeters: string;
  closeMeters: string;
  openRate: number;
  closeRate: number;
}

interface WorkLogPageProps {
  user: User;
}

export default function WorkLogPage({ user }: WorkLogPageProps) {
  const { toast } = useToast();
  const [selectedDate, setSelectedDate] = useState<Date>(new Date());
  const [selectedProjectId, setSelectedProjectId] = useState<string>("");
  const [workEntries, setWorkEntries] = useState<WorkLogEntry[]>([]);

  // Track which project we've initialized entries for (prevents refetch resets)
  const initializedProjectRef = useRef<string>("");

  // Check if selected date is today (for edit restrictions)
  const isToday = selectedDate.toDateString() === new Date().toDateString();

  // Fetch user's supervised projects
  const { data: projects, isLoading: projectsLoading } = useQuery<Project[]>({
    queryKey: ["/api/projects"],
  });

  // Fetch labourers for selected project
  const { data: labourers, isLoading: labourersLoading } = useQuery<Labourer[]>({
    queryKey: selectedProjectId ? [`/api/projects/${selectedProjectId}/labourers`] : [],
    enabled: !!selectedProjectId,
  });

  // Fetch pay rates for selected project
  const { data: payRates } = useQuery<PayRate[]>({
    queryKey: selectedProjectId ? [`/api/projects/${selectedProjectId}/pay-rates`] : [],
    enabled: !!selectedProjectId,
  });

  // Initialize/update work entries when project data changes
  useEffect(() => {
    if (!selectedProjectId) {
      // Clear entries when no project selected
      setWorkEntries([]);
      initializedProjectRef.current = "";
      return;
    }

    if (!labourers || !payRates) {
      return;
    }

    // Check if this is a new project
    const isNewProject = initializedProjectRef.current !== selectedProjectId;

    if (isNewProject) {
      // Initialize fresh entries for new project
      const entries = labourers.map(labourer => {
        const openRate = payRates.find(
          rate => rate.employeeTypeId === labourer.employeeTypeId && rate.category === "open_trenching"
        );
        const closeRate = payRates.find(
          rate => rate.employeeTypeId === labourer.employeeTypeId && rate.category === "close_trenching"
        );

        return {
          labourerId: labourer.id,
          labourerName: `${labourer.firstName} ${labourer.surname}`,
          employeeTypeId: labourer.employeeTypeId,
          openMeters: "0",
          closeMeters: "0",
          openRate: openRate ? parseFloat(openRate.amount) : 0,
          closeRate: closeRate ? parseFloat(closeRate.amount) : 0,
        };
      });
      setWorkEntries(entries);
      initializedProjectRef.current = selectedProjectId;
    } else {
      // Merge: preserve meter inputs, update rates, add new labourers
      setWorkEntries(prevEntries => {
        const entryMap = new Map(prevEntries.map(e => [e.labourerId, e]));

        return labourers.map(labourer => {
          const existing = entryMap.get(labourer.id);
          const openRate = payRates.find(
            rate => rate.employeeTypeId === labourer.employeeTypeId && rate.category === "open_trenching"
          );
          const closeRate = payRates.find(
            rate => rate.employeeTypeId === labourer.employeeTypeId && rate.category === "close_trenching"
          );

          return {
            labourerId: labourer.id,
            labourerName: `${labourer.firstName} ${labourer.surname}`,
            employeeTypeId: labourer.employeeTypeId,
            // Preserve existing meter inputs or default to "0" for new labourers
            openMeters: existing?.openMeters ?? "0",
            closeMeters: existing?.closeMeters ?? "0",
            // Always use latest pay rates
            openRate: openRate ? parseFloat(openRate.amount) : 0,
            closeRate: closeRate ? parseFloat(closeRate.amount) : 0,
          };
        });
      });
    }
  }, [labourers, payRates, selectedProjectId]);

  const saveWorkLogsMutation = useMutation({
    mutationFn: async (entries: WorkLogEntry[]) => {
      const promises = entries
        .filter(entry => parseFloat(entry.openMeters) > 0 || parseFloat(entry.closeMeters) > 0)
        .map(entry => {
          const openMeters = parseFloat(entry.openMeters) || 0;
          const closeMeters = parseFloat(entry.closeMeters) || 0;
          const totalEarnings = (openMeters * entry.openRate) + (closeMeters * entry.closeRate);

          return apiRequest("POST", "/api/work-logs", {
            projectId: selectedProjectId,
            labourerId: entry.labourerId,
            workDate: format(selectedDate, "yyyy-MM-dd"),
            openTrenchingMeters: openMeters.toString(),
            closeTrenchingMeters: closeMeters.toString(),
            totalEarnings: totalEarnings.toString(),
          });
        });

      return Promise.all(promises);
    },
    onSuccess: () => {
      toast({
        title: "Work logs saved",
        description: `Successfully saved work logs for ${format(selectedDate, "PPP")}`,
      });
      // Reset entries
      setWorkEntries(prev => prev.map(entry => ({ ...entry, openMeters: "0", closeMeters: "0" })));
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to save work logs",
        variant: "destructive",
      });
    },
  });

  const handleMeterChange = (labourerId: string, field: "openMeters" | "closeMeters", value: string) => {
    setWorkEntries(prev =>
      prev.map(entry =>
        entry.labourerId === labourerId ? { ...entry, [field]: value } : entry
      )
    );
  };

  const calculateEarnings = (entry: WorkLogEntry) => {
    const openMeters = parseFloat(entry.openMeters) || 0;
    const closeMeters = parseFloat(entry.closeMeters) || 0;
    return (openMeters * entry.openRate) + (closeMeters * entry.closeRate);
  };

  const totalDailyEarnings = workEntries.reduce((sum, entry) => sum + calculateEarnings(entry), 0);

  const handleSave = () => {
    if (!selectedProjectId) {
      toast({
        title: "No project selected",
        description: "Please select a project first",
        variant: "destructive",
      });
      return;
    }

    // Enforce that only today's work can be saved (client-side guard)
    if (!isToday) {
      toast({
        title: "Cannot Edit Historical Date",
        description: "You can only edit today's work logs. Please submit a correction request to modify historical data.",
        variant: "destructive",
      });
      return;
    }

    if (workEntries.every(e => parseFloat(e.openMeters) === 0 && parseFloat(e.closeMeters) === 0)) {
      toast({
        title: "No work entered",
        description: "Please enter at least one meter reading",
        variant: "destructive",
      });
      return;
    }

    saveWorkLogsMutation.mutate(workEntries);
  };

  const selectedProject = projects?.find(p => p.id === selectedProjectId);

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-semibold">Daily Work Log</h1>
        <p className="text-sm text-muted-foreground mt-1">
          Record daily opening and closing trench meters for labourers
        </p>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Work Entry</CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="project">Project</Label>
              <Select value={selectedProjectId} onValueChange={(value) => {
                setSelectedProjectId(value);
                setWorkEntries([]);
              }} disabled={projectsLoading}>
                <SelectTrigger id="project" data-testid="select-project">
                  <SelectValue placeholder={projectsLoading ? "Loading projects..." : "Select a project"} />
                </SelectTrigger>
                <SelectContent>
                  {projects?.map((project) => (
                    <SelectItem key={project.id} value={project.id}>
                      {project.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label>Work Date</Label>
              <Popover>
                <PopoverTrigger asChild>
                  <Button variant="outline" className="w-full justify-start" data-testid="button-select-date">
                    <CalendarIcon className="mr-2 h-4 w-4" />
                    {format(selectedDate, "PPP")}
                  </Button>
                </PopoverTrigger>
                <PopoverContent className="w-auto p-0" align="start">
                  <Calendar
                    mode="single"
                    selected={selectedDate}
                    onSelect={(date) => date && setSelectedDate(date)}
                    disabled={(date) => date > new Date()}
                  />
                </PopoverContent>
              </Popover>
            </div>
          </div>

          {selectedProjectId && (
            <>
              {labourersLoading ? (
                <div className="flex items-center justify-center py-12">
                  <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
                </div>
              ) : workEntries.length > 0 ? (
                <div className="space-y-4">
                  {!isToday && (
                    <div className="bg-yellow-50 dark:bg-yellow-950 border border-yellow-200 dark:border-yellow-800 rounded-md p-4">
                      <p className="text-sm text-yellow-800 dark:text-yellow-200">
                        <strong>Historical Date Selected:</strong> You can only edit today's work logs. To modify historical data, please submit a correction request via the Audit Trail page.
                      </p>
                    </div>
                  )}
                  <div className="rounded-md border">
                    <Table>
                      <TableHeader>
                        <TableRow>
                          <TableHead>Labourer</TableHead>
                          <TableHead className="text-right">Open Trenching (m)</TableHead>
                          <TableHead className="text-right">Close Trenching (m)</TableHead>
                          <TableHead className="text-right">Daily Earnings</TableHead>
                        </TableRow>
                      </TableHeader>
                      <TableBody>
                        {workEntries.map((entry) => (
                          <TableRow key={entry.labourerId} data-testid={`row-labourer-${entry.labourerId}`}>
                            <TableCell className="font-medium">{entry.labourerName}</TableCell>
                            <TableCell className="text-right">
                              <Input
                                type="number"
                                step="0.1"
                                min="0"
                                value={entry.openMeters}
                                onChange={(e) => handleMeterChange(entry.labourerId, "openMeters", e.target.value)}
                                className="w-28 text-right font-mono"
                                data-testid={`input-open-meters-${entry.labourerId}`}
                                disabled={!isToday}
                              />
                            </TableCell>
                            <TableCell className="text-right">
                              <Input
                                type="number"
                                step="0.1"
                                min="0"
                                value={entry.closeMeters}
                                onChange={(e) => handleMeterChange(entry.labourerId, "closeMeters", e.target.value)}
                                className="w-28 text-right font-mono"
                                data-testid={`input-close-meters-${entry.labourerId}`}
                                disabled={!isToday}
                              />
                            </TableCell>
                            <TableCell className="text-right font-mono text-green-600 dark:text-green-400">
                              R {calculateEarnings(entry).toFixed(2)}
                            </TableCell>
                          </TableRow>
                        ))}
                        <TableRow className="bg-muted/50">
                          <TableCell colSpan={3} className="font-semibold text-right">Total Daily Earnings:</TableCell>
                          <TableCell className="text-right font-mono font-semibold text-green-600 dark:text-green-400">
                            R {totalDailyEarnings.toFixed(2)}
                          </TableCell>
                        </TableRow>
                      </TableBody>
                    </Table>
                  </div>

                  <div className="flex justify-end">
                    <Button
                      onClick={handleSave}
                      disabled={!isToday || saveWorkLogsMutation.isPending}
                      data-testid="button-save-work-log"
                    >
                      {saveWorkLogsMutation.isPending && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                      <Save className="mr-2 h-4 w-4" />
                      {isToday ? "Save Work Log" : "Cannot Edit Historical Date"}
                    </Button>
                  </div>
                </div>
              ) : (
                <div className="text-center py-12 text-muted-foreground">
                  No labourers found for this project
                </div>
              )}
            </>
          )}

          {!selectedProjectId && (
            <div className="text-center py-12 text-muted-foreground">
              Select a project to start logging daily work
            </div>
          )}
        </CardContent>
      </Card>

      {selectedProject && (
        <Card>
          <CardHeader>
            <CardTitle>Project Details</CardTitle>
          </CardHeader>
          <CardContent className="grid grid-cols-2 gap-4">
            <div>
              <p className="text-sm text-muted-foreground">Project Name</p>
              <p className="font-medium">{selectedProject.name}</p>
            </div>
            <div>
              <p className="text-sm text-muted-foreground">Location</p>
              <p className="font-medium">{selectedProject.location || "N/A"}</p>
            </div>
            <div>
              <p className="text-sm text-muted-foreground">Total Labourers</p>
              <p className="font-medium">{workEntries.length}</p>
            </div>
            <div>
              <p className="text-sm text-muted-foreground">Work Date</p>
              <p className="font-medium">{format(selectedDate, "PPP")}</p>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

