import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
  FormDescription,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Plus, Loader2, CheckCircle, XCircle, FileText, Clock } from "lucide-react";
import { insertCorrectionRequestSchema } from "@shared/schema";
import type { User, CorrectionRequest } from "@shared/schema";
import type { z } from "zod";
import { format, parseISO } from "date-fns";

interface AuditPageProps {
  user: User;
}

type CorrectionRequestFormData = z.infer<typeof insertCorrectionRequestSchema>;

const statusColors: Record<string, string> = {
  pending: "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200",
  approved: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
  rejected: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
};

const statusLabels: Record<string, string> = {
  pending: "Pending",
  approved: "Approved",
  rejected: "Rejected",
};

export default function AuditPage({ user }: AuditPageProps) {
  const { toast } = useToast();
  const [createDialogOpen, setCreateDialogOpen] = useState(false);
  const [detailsDialogOpen, setDetailsDialogOpen] = useState(false);
  const [reviewDialogOpen, setReviewDialogOpen] = useState(false);
  const [selectedRequest, setSelectedRequest] = useState<CorrectionRequest | null>(null);
  const [selectedStatus, setSelectedStatus] = useState<string>("all");
  const [reviewNotes, setReviewNotes] = useState("");

  const canCreate = true; // All authenticated users can create correction requests
  const canReview = user.role === "super_admin" || user.role === "admin" || user.role === "project_manager";

  const createForm = useForm<CorrectionRequestFormData>({
    resolver: zodResolver(insertCorrectionRequestSchema),
    defaultValues: {
      entityType: "",
      entityId: "",
      fieldName: "",
      oldValue: "",
      newValue: "",
      reason: "",
      requestedBy: "",
    },
  });

  const { data: requests, isLoading } = useQuery<CorrectionRequest[]>({
    queryKey: ["/api/correction-requests"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: CorrectionRequestFormData) => {
      // Populate requestedBy with current user ID
      const payload = {
        ...data,
        requestedBy: user.id,
      };
      return apiRequest("POST", "/api/correction-requests", payload);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/correction-requests"] });
      toast({
        title: "Success",
        description: "Correction request submitted successfully",
      });
      setCreateDialogOpen(false);
      createForm.reset();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create correction request",
        variant: "destructive",
      });
    },
  });

  const reviewMutation = useMutation({
    mutationFn: async ({ id, status, notes }: { id: string; status: string; notes: string }) => {
      return apiRequest("PUT", `/api/correction-requests/${id}`, {
        status,
        reviewedBy: user.id,
        reviewedAt: new Date().toISOString(),
        reviewNotes: notes,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/correction-requests"] });
      toast({
        title: "Success",
        description: "Correction request reviewed successfully",
      });
      setReviewDialogOpen(false);
      setReviewNotes("");
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to review correction request",
        variant: "destructive",
      });
    },
  });

  const filteredRequests = requests?.filter(request => {
    if (selectedStatus !== "all" && request.status !== selectedStatus) return false;
    return true;
  }) || [];

  const handleViewDetails = (request: CorrectionRequest) => {
    setSelectedRequest(request);
    setDetailsDialogOpen(true);
  };

  const handleReview = (request: CorrectionRequest) => {
    setSelectedRequest(request);
    setReviewDialogOpen(true);
  };

  const handleApprove = () => {
    if (!selectedRequest) return;
    reviewMutation.mutate({
      id: selectedRequest.id,
      status: "approved",
      notes: reviewNotes,
    });
  };

  const handleReject = () => {
    if (!selectedRequest) return;
    reviewMutation.mutate({
      id: selectedRequest.id,
      status: "rejected",
      notes: reviewNotes,
    });
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">Audit Trail</h1>
          <p className="text-muted-foreground">Track and manage data correction requests</p>
        </div>
        {canCreate && (
          <Button onClick={() => setCreateDialogOpen(true)} data-testid="button-create-request">
            <Plus className="mr-2 h-4 w-4" />
            New Request
          </Button>
        )}
      </div>

      <div className="flex gap-4">
        <Select value={selectedStatus} onValueChange={setSelectedStatus}>
          <SelectTrigger className="w-48" data-testid="select-status-filter">
            <SelectValue placeholder="Filter by status" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Statuses</SelectItem>
            <SelectItem value="pending">Pending</SelectItem>
            <SelectItem value="approved">Approved</SelectItem>
            <SelectItem value="rejected">Rejected</SelectItem>
          </SelectContent>
        </Select>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Correction Requests</CardTitle>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="flex justify-center p-8">
              <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
            </div>
          ) : filteredRequests.length === 0 ? (
            <div className="text-center p-8 text-muted-foreground">
              No correction requests found
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Entity Type</TableHead>
                  <TableHead>Field</TableHead>
                  <TableHead>Change</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Requested</TableHead>
                  <TableHead className="text-right">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredRequests.map(request => (
                  <TableRow key={request.id} data-testid={`row-request-${request.id}`}>
                    <TableCell className="font-medium">
                      {request.entityType}
                    </TableCell>
                    <TableCell>{request.fieldName}</TableCell>
                    <TableCell>
                      <div className="max-w-xs truncate">
                        <span className="text-muted-foreground line-through">{request.oldValue || "N/A"}</span>
                        {"  "}
                        <span className="font-medium">{request.newValue}</span>
                      </div>
                    </TableCell>
                    <TableCell>
                      <Badge className={statusColors[request.status]} data-testid={`status-${request.id}`}>
                        {statusLabels[request.status]}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        <Clock className="h-4 w-4 text-muted-foreground" />
                        <span className="text-sm">
                          {request.requestedAt && format(typeof request.requestedAt === 'string' ? parseISO(request.requestedAt) : request.requestedAt, "MMM d, yyyy")}
                        </span>
                      </div>
                    </TableCell>
                    <TableCell className="text-right">
                      <div className="flex justify-end gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleViewDetails(request)}
                          data-testid={`button-view-${request.id}`}
                        >
                          <FileText className="mr-2 h-4 w-4" />
                          Details
                        </Button>
                        {canReview && request.status === "pending" && (
                          <Button
                            variant="default"
                            size="sm"
                            onClick={() => handleReview(request)}
                            data-testid={`button-review-${request.id}`}
                          >
                            Review
                          </Button>
                        )}
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>

      {/* Create Correction Request Dialog */}
      <Dialog open={createDialogOpen} onOpenChange={setCreateDialogOpen}>
        <DialogContent data-testid="dialog-create-request">
          <DialogHeader>
            <DialogTitle>Create Correction Request</DialogTitle>
            <DialogDescription>
              Request a change to existing data with proper approval workflow
            </DialogDescription>
          </DialogHeader>
          <Form {...createForm}>
            <form onSubmit={createForm.handleSubmit((data) => createMutation.mutate(data))} className="space-y-4">
              <FormField
                control={createForm.control}
                name="entityType"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Entity Type</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-entity-type">
                          <SelectValue placeholder="Select entity type" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="work_log">Work Log</SelectItem>
                        <SelectItem value="labourer">Labourer</SelectItem>
                        <SelectItem value="project">Project</SelectItem>
                        <SelectItem value="payment_period">Payment Period</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={createForm.control}
                name="entityId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Entity ID</FormLabel>
                    <FormControl>
                      <Input placeholder="Enter entity ID" {...field} data-testid="input-entity-id" />
                    </FormControl>
                    <FormDescription>
                      The ID of the record you want to change
                    </FormDescription>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={createForm.control}
                name="fieldName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Field Name</FormLabel>
                    <FormControl>
                      <Input placeholder="e.g., openTrenchingMeters" {...field} data-testid="input-field-name" />
                    </FormControl>
                    <FormDescription>
                      The name of the field to change
                    </FormDescription>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={createForm.control}
                name="oldValue"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Current Value</FormLabel>
                    <FormControl>
                      <Input placeholder="Current value" {...field} value={field.value || ""} data-testid="input-old-value" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={createForm.control}
                name="newValue"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>New Value</FormLabel>
                    <FormControl>
                      <Input placeholder="Desired new value" {...field} data-testid="input-new-value" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={createForm.control}
                name="reason"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Reason for Change</FormLabel>
                    <FormControl>
                      <Textarea
                        placeholder="Explain why this change is needed"
                        {...field}
                        data-testid="input-reason"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <DialogFooter>
                <Button type="button" variant="outline" onClick={() => setCreateDialogOpen(false)}>
                  Cancel
                </Button>
                <Button type="submit" disabled={createMutation.isPending} data-testid="button-submit-create">
                  {createMutation.isPending && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                  Submit Request
                </Button>
              </DialogFooter>
            </form>
          </Form>
        </DialogContent>
      </Dialog>

      {/* Details Dialog */}
      <Dialog open={detailsDialogOpen} onOpenChange={setDetailsDialogOpen}>
        <DialogContent data-testid="dialog-request-details">
          <DialogHeader>
            <DialogTitle>Correction Request Details</DialogTitle>
          </DialogHeader>

          {selectedRequest && (
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-sm text-muted-foreground">Status</p>
                  <Badge className={statusColors[selectedRequest.status]}>
                    {statusLabels[selectedRequest.status]}
                  </Badge>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Requested</p>
                  <p>{selectedRequest.requestedAt && format(typeof selectedRequest.requestedAt === 'string' ? parseISO(selectedRequest.requestedAt) : selectedRequest.requestedAt, "MMM d, yyyy HH:mm")}</p>
                </div>
              </div>

              <div>
                <p className="text-sm text-muted-foreground">Entity</p>
                <p className="font-medium">{selectedRequest.entityType} (ID: {selectedRequest.entityId})</p>
              </div>

              <div>
                <p className="text-sm text-muted-foreground">Field</p>
                <p className="font-medium">{selectedRequest.fieldName}</p>
              </div>

              <div>
                <p className="text-sm text-muted-foreground">Change</p>
                <div className="p-3 bg-muted rounded-md">
                  <p className="text-sm">
                    <span className="text-muted-foreground line-through">{selectedRequest.oldValue || "N/A"}</span>
                    {"  "}
                    <span className="font-medium text-primary">{selectedRequest.newValue}</span>
                  </p>
                </div>
              </div>

              <div>
                <p className="text-sm text-muted-foreground">Reason</p>
                <p className="text-sm">{selectedRequest.reason}</p>
              </div>

              {selectedRequest.reviewedBy && (
                <>
                  <div>
                    <p className="text-sm text-muted-foreground">Reviewed</p>
                    <p>{selectedRequest.reviewedAt ? format(typeof selectedRequest.reviewedAt === 'string' ? parseISO(selectedRequest.reviewedAt) : selectedRequest.reviewedAt, "MMM d, yyyy HH:mm") : "N/A"}</p>
                  </div>
                  {selectedRequest.reviewNotes && (
                    <div>
                      <p className="text-sm text-muted-foreground">Review Notes</p>
                      <p className="text-sm">{selectedRequest.reviewNotes}</p>
                    </div>
                  )}
                </>
              )}
            </div>
          )}

          <DialogFooter>
            <Button variant="outline" onClick={() => setDetailsDialogOpen(false)}>
              Close
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Review Dialog */}
      <Dialog open={reviewDialogOpen} onOpenChange={setReviewDialogOpen}>
        <DialogContent data-testid="dialog-review-request">
          <DialogHeader>
            <DialogTitle>Review Correction Request</DialogTitle>
            <DialogDescription>
              Approve or reject this data change request
            </DialogDescription>
          </DialogHeader>

          {selectedRequest && (
            <div className="space-y-4">
              <div>
                <p className="text-sm text-muted-foreground">Change</p>
                <div className="p-3 bg-muted rounded-md">
                  <p className="text-sm font-mono">
                    <span className="text-muted-foreground line-through">{selectedRequest.oldValue || "N/A"}</span>
                    {"  "}
                    <span className="font-medium text-primary">{selectedRequest.newValue}</span>
                  </p>
                </div>
              </div>

              <div>
                <p className="text-sm text-muted-foreground">Reason</p>
                <p className="text-sm">{selectedRequest.reason}</p>
              </div>

              <div>
                <label className="text-sm font-medium">Review Notes (optional)</label>
                <Textarea
                  value={reviewNotes}
                  onChange={(e) => setReviewNotes(e.target.value)}
                  placeholder="Add any notes about your decision"
                  data-testid="input-review-notes"
                />
              </div>
            </div>
          )}

          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setReviewDialogOpen(false);
                setReviewNotes("");
              }}
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={handleReject}
              disabled={reviewMutation.isPending}
              data-testid="button-reject-review"
            >
              <XCircle className="mr-2 h-4 w-4" />
              Reject
            </Button>
            <Button
              variant="default"
              onClick={handleApprove}
              disabled={reviewMutation.isPending}
              data-testid="button-approve-review"
            >
              <CheckCircle className="mr-2 h-4 w-4" />
              Approve
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/payments.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Plus, Loader2, CheckCircle, XCircle, Send, Calendar, DollarSign, Download } from "lucide-react";
import { insertPaymentPeriodSchema } from "@shared/schema";
import type { User, Project, PaymentPeriod, PaymentPeriodEntry, Labourer } from "@shared/schema";
import type { z } from "zod";
import { format, parseISO } from "date-fns";

interface PaymentsPageProps {
  user: User;
}

type PaymentPeriodFormData = z.infer<typeof insertPaymentPeriodSchema>;

const statusColors: Record<string, string> = {
  open: "bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200",
  submitted: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200",
  approved: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
  rejected: "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200",
  paid: "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200",
};

const statusLabels: Record<string, string> = {
  open: "Open",
  submitted: "Submitted",
  approved: "Approved",
  rejected: "Rejected",
  paid: "Paid",
};

export default function PaymentsPage({ user }: PaymentsPageProps) {
  const { toast } = useToast();
  const [createDialogOpen, setCreateDialogOpen] = useState(false);
  const [detailsDialogOpen, setDetailsDialogOpen] = useState(false);
  const [selectedPeriod, setSelectedPeriod] = useState<PaymentPeriod | null>(null);
  const [selectedProjectId, setSelectedProjectId] = useState<string>("all");
  const [selectedStatus, setSelectedStatus] = useState<string>("all");

  const canCreate = user.role === "super_admin" || user.role === "admin" || user.role === "project_manager";
  const canApprove = user.role === "super_admin" || user.role === "admin" || user.role === "project_manager";
  const canSubmit = true; // Any authenticated user can submit

  const createForm = useForm<PaymentPeriodFormData>({
    resolver: zodResolver(insertPaymentPeriodSchema),
    defaultValues: {
      projectId: "",
      startDate: "",
      endDate: "",
    },
  });

  const { data: projects } = useQuery<Project[]>({
    queryKey: ["/api/projects"],
  });

  // Get all payment periods across all projects
  const { data: allPeriods, isLoading } = useQuery<PaymentPeriod[]>({
    queryKey: ["/api/payment-periods/all"],
    queryFn: async () => {
      if (!projects || projects.length === 0) return [];

      const periodPromises = projects.map(project =>
        fetch(`/api/projects/${project.id}/payment-periods`)
          .then(res => res.ok ? res.json() : [])
      );

      const results = await Promise.all(periodPromises);
      return results.flat();
    },
    enabled: !!projects,
  });

  const { data: entries } = useQuery<(PaymentPeriodEntry & { labourer?: Labourer })[]>({
    queryKey: ["/api/payment-periods", selectedPeriod?.id, "entries"],
    queryFn: async () => {
      if (!selectedPeriod) return [];
      const response = await fetch(`/api/payment-periods/${selectedPeriod.id}/entries`);
      if (!response.ok) return [];
      return response.json();
    },
    enabled: !!selectedPeriod && detailsDialogOpen,
  });

  const createMutation = useMutation({
    mutationFn: async (data: PaymentPeriodFormData) => {
      // Backend will auto-calculate totalAmount from work logs
      return apiRequest("POST", "/api/payment-periods", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/payment-periods/all"] });
      toast({
        title: "Success",
        description: "Payment period created successfully",
      });
      setCreateDialogOpen(false);
      createForm.reset();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create payment period",
        variant: "destructive",
      });
    },
  });

  const updateStatusMutation = useMutation({
    mutationFn: async ({ id, status, userId }: { id: string; status: string; userId: string }) => {
      const updateData: any = { status };

      if (status === "submitted") {
        updateData.submittedBy = userId;
        updateData.submittedAt = new Date().toISOString();
      } else if (status === "approved") {
        updateData.approvedBy = userId;
        updateData.approvedAt = new Date().toISOString();
      }

      return apiRequest("PUT", `/api/payment-periods/${id}`, updateData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/payment-periods/all"] });
      toast({
        title: "Success",
        description: "Payment period status updated successfully",
      });
      setDetailsDialogOpen(false);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update payment period",
        variant: "destructive",
      });
    },
  });

  const filteredPeriods = allPeriods?.filter(period => {
    if (selectedProjectId !== "all" && period.projectId !== selectedProjectId) return false;
    if (selectedStatus !== "all" && period.status !== selectedStatus) return false;
    return true;
  }) || [];

  const getProjectName = (projectId: string) => {
    return projects?.find(p => p.id === projectId)?.name || "Unknown Project";
  };

  const handleViewDetails = (period: PaymentPeriod) => {
    setSelectedPeriod(period);
    setDetailsDialogOpen(true);
  };

  const handleSubmit = (period: PaymentPeriod) => {
    updateStatusMutation.mutate({
      id: period.id,
      status: "submitted",
      userId: user.id,
    });
  };

  const handleApprove = (period: PaymentPeriod) => {
    updateStatusMutation.mutate({
      id: period.id,
      status: "approved",
      userId: user.id,
    });
  };

  const handleReject = (period: PaymentPeriod) => {
    updateStatusMutation.mutate({
      id: period.id,
      status: "rejected",
      userId: user.id,
    });
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">Payment Periods</h1>
          <p className="text-muted-foreground">Manage fortnightly payment cycles and approvals</p>
        </div>
        {canCreate && (
          <Button onClick={() => setCreateDialogOpen(true)} data-testid="button-create-period">
            <Plus className="mr-2 h-4 w-4" />
            Create Period
          </Button>
        )}
      </div>

      <div className="flex gap-4">
        <Select value={selectedProjectId} onValueChange={setSelectedProjectId}>
          <SelectTrigger className="w-64" data-testid="select-project-filter">
            <SelectValue placeholder="Filter by project" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Projects</SelectItem>
            {projects?.map(project => (
              <SelectItem key={project.id} value={project.id}>
                {project.name}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>

        <Select value={selectedStatus} onValueChange={setSelectedStatus}>
          <SelectTrigger className="w-48" data-testid="select-status-filter">
            <SelectValue placeholder="Filter by status" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Statuses</SelectItem>
            <SelectItem value="open">Open</SelectItem>
            <SelectItem value="submitted">Submitted</SelectItem>
            <SelectItem value="approved">Approved</SelectItem>
            <SelectItem value="rejected">Rejected</SelectItem>
            <SelectItem value="paid">Paid</SelectItem>
          </SelectContent>
        </Select>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Payment Periods</CardTitle>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="flex justify-center p-8">
              <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
            </div>
          ) : filteredPeriods.length === 0 ? (
            <div className="text-center p-8 text-muted-foreground">
              No payment periods found
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Project</TableHead>
                  <TableHead>Period</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead className="text-right">Total Amount</TableHead>
                  <TableHead className="text-right">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredPeriods.map(period => (
                  <TableRow key={period.id} data-testid={`row-period-${period.id}`}>
                    <TableCell className="font-medium">
                      {getProjectName(period.projectId)}
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        <Calendar className="h-4 w-4 text-muted-foreground" />
                        <span>
                          {format(parseISO(period.startDate), "MMM d, yyyy")} - {format(parseISO(period.endDate), "MMM d, yyyy")}
                        </span>
                      </div>
                    </TableCell>
                    <TableCell>
                      <Badge className={statusColors[period.status]} data-testid={`status-${period.id}`}>
                        {statusLabels[period.status]}
                      </Badge>
                    </TableCell>
                    <TableCell className="text-right font-mono">
                      R {parseFloat(period.totalAmount).toFixed(2)}
                    </TableCell>
                    <TableCell className="text-right">
                      <div className="flex justify-end gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleViewDetails(period)}
                          data-testid={`button-view-${period.id}`}
                        >
                          View Details
                        </Button>
                        {canSubmit && period.status === "open" && (
                          <Button
                            variant="default"
                            size="sm"
                            onClick={() => handleSubmit(period)}
                            disabled={updateStatusMutation.isPending}
                            data-testid={`button-submit-${period.id}`}
                          >
                            <Send className="mr-2 h-4 w-4" />
                            Submit
                          </Button>
                        )}
                        {canApprove && period.status === "submitted" && (
                          <>
                            <Button
                              variant="default"
                              size="sm"
                              onClick={() => handleApprove(period)}
                              disabled={updateStatusMutation.isPending}
                              data-testid={`button-approve-${period.id}`}
                            >
                              <CheckCircle className="mr-2 h-4 w-4" />
                              Approve
                            </Button>
                            <Button
                              variant="destructive"
                              size="sm"
                              onClick={() => handleReject(period)}
                              disabled={updateStatusMutation.isPending}
                              data-testid={`button-reject-${period.id}`}
                            >
                              <XCircle className="mr-2 h-4 w-4" />
                              Reject
                            </Button>
                          </>
                        )}
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>

      {/* Create Payment Period Dialog */}
      <Dialog open={createDialogOpen} onOpenChange={setCreateDialogOpen}>
        <DialogContent data-testid="dialog-create-period">
          <DialogHeader>
            <DialogTitle>Create Payment Period</DialogTitle>
            <DialogDescription>
              Create a new payment period for tracking worker earnings
            </DialogDescription>
          </DialogHeader>
          <Form {...createForm}>
            <form onSubmit={createForm.handleSubmit((data) => createMutation.mutate(data))} className="space-y-4">
              <FormField
                control={createForm.control}
                name="projectId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Project</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-project">
                          <SelectValue placeholder="Select project" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {projects?.map(project => (
                          <SelectItem key={project.id} value={project.id}>
                            {project.name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={createForm.control}
                name="startDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Start Date</FormLabel>
                    <FormControl>
                      <Input type="date" {...field} data-testid="input-start-date" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={createForm.control}
                name="endDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>End Date</FormLabel>
                    <FormControl>
                      <Input type="date" {...field} data-testid="input-end-date" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="bg-muted p-4 rounded-md">
                <p className="text-sm text-muted-foreground">
                  <strong>Note:</strong> The total amount will be automatically calculated from work logs in this date range.
                </p>
              </div>

              <DialogFooter>
                <Button type="button" variant="outline" onClick={() => setCreateDialogOpen(false)}>
                  Cancel
                </Button>
                <Button type="submit" disabled={createMutation.isPending} data-testid="button-submit-create">
                  {createMutation.isPending && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                  Create Period
                </Button>
              </DialogFooter>
            </form>
          </Form>
        </DialogContent>
      </Dialog>

      {/* Period Details Dialog */}
      <Dialog open={detailsDialogOpen} onOpenChange={setDetailsDialogOpen}>
        <DialogContent className="max-w-4xl max-h-[90vh] flex flex-col" data-testid="dialog-period-details">
          <DialogHeader>
            <DialogTitle>Payment Period Details</DialogTitle>
            <DialogDescription>
              {selectedPeriod && (
                <>
                  {getProjectName(selectedPeriod.projectId)} | {format(parseISO(selectedPeriod.startDate), "MMM d, yyyy")} - {format(parseISO(selectedPeriod.endDate), "MMM d, yyyy")}
                </>
              )}
            </DialogDescription>
          </DialogHeader>

          <div className="flex-1 overflow-y-auto">
            {selectedPeriod && (
              <div className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <p className="text-sm text-muted-foreground">Status</p>
                    <Badge className={statusColors[selectedPeriod.status]}>
                      {statusLabels[selectedPeriod.status]}
                    </Badge>
                  </div>
                  <div>
                    <p className="text-sm text-muted-foreground">Total Amount</p>
                    <p className="text-2xl font-bold">
                      R {entries ? entries.reduce((sum, entry) => sum + parseFloat(entry.totalEarnings), 0).toFixed(2) : "0.00"}
                    </p>
                  </div>
                </div>

                {entries && entries.length > 0 && (
                  <div>
                    <h3 className="font-semibold mb-2">Payment Entries</h3>
                    <div className="border rounded-md">
                      <Table>
                        <TableHeader>
                          <TableRow>
                            <TableHead>Labourer</TableHead>
                            <TableHead>Days Worked</TableHead>
                            <TableHead className="text-right">Open (m)</TableHead>
                            <TableHead className="text-right">Close (m)</TableHead>
                            <TableHead className="text-right">Total (m)</TableHead>
                            <TableHead className="text-right">Earnings</TableHead>
                          </TableRow>
                        </TableHeader>
                        <TableBody>
                          {entries.map(entry => (
                            <TableRow key={entry.id}>
                              <TableCell>{entry.labourerId}</TableCell>
                              <TableCell>{entry.daysWorked}</TableCell>
                              <TableCell className="text-right font-mono">{parseFloat(entry.openMeters || "0").toFixed(2)}</TableCell>
                              <TableCell className="text-right font-mono">{parseFloat(entry.closeMeters || "0").toFixed(2)}</TableCell>
                              <TableCell className="text-right font-mono">{parseFloat(entry.totalMeters).toFixed(2)}</TableCell>
                              <TableCell className="text-right font-mono">
                                R {parseFloat(entry.totalEarnings).toFixed(2)}
                              </TableCell>
                            </TableRow>
                          ))}
                        </TableBody>
                      </Table>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>

          <DialogFooter className="flex-col sm:flex-row gap-2">
            <Button 
              variant="default"
              onClick={async () => {
                if (!selectedPeriod) return;
                try {
                  const response = await fetch(`/api/payment-periods/${selectedPeriod.id}/payment-file`);
                  if (!response.ok) {
                    const error = await response.json();
                    toast({
                      title: "Error generating payment file",
                      description: error.message || "Failed to generate payment file",
                      variant: "destructive",
                    });
                    return;
                  }
                  // Get filename from Content-Disposition header
                  const contentDisposition = response.headers.get('Content-Disposition');
                  const filenameMatch = contentDisposition?.match(/filename="(.+)"/);
                  const filename = filenameMatch ? filenameMatch[1] : 'payment_file.csv';

                  // Download the file
                  const blob = await response.blob();
                  const url = window.URL.createObjectURL(blob);
                  const a = document.createElement('a');
                  a.href = url;
                  a.download = filename;
                  document.body.appendChild(a);
                  a.click();
                  window.URL.revokeObjectURL(url);
                  document.body.removeChild(a);

                  toast({
                    title: "Payment file downloaded",
                    description: `Successfully generated ${filename}`,
                  });
                } catch (error: any) {
                  toast({
                    title: "Download failed",
                    description: error.message || "Failed to download payment file",
                    variant: "destructive",
                  });
                }
              }}
              data-testid="button-download-payment-file"
            >
              <Download className="h-4 w-4 mr-2" />
              Download Payment File
            </Button>
            <Button variant="outline" onClick={() => setDetailsDialogOpen(false)}>
              Close
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

