];

// Foreign Names (Zimbabwe, Mozambique, Lesotho)
const foreignNames = [
  { first: "Tendai", last: "Moyo", country: "Zimbabwe" },
  { first: "Farai", last: "Ncube", country: "Zimbabwe" },
